import classes from "./AvailabelMeals.module.css";
import Card from "../UI/Card";
import Mealitem from "./Mealitem/MealItem";

const Meals = (callback) => {
  setTimeout(async () => {
    const Arr = await fetch(
      "https://fooddelivery-665ca-default-rtdb.asia-southeast1.firebasedatabase.app/Meals.json"
    );
    const meal = await Arr.json();
    callback(meal);
  }, 5000);
};

const meals1 = Meals((meals1) => {
  return meals1;
});

console.log(meals1);

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 183.92,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 132.0,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 103.92,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 151.92,
  },
];

const AvailableMeals = () => {
  const [Meals, setMeals] = useState([]);

  const mealitems = Meals.map((meal) => {
    return (
      <Mealitem
        key={meal.id}
        id={meal.id}
        name={meal.name}
        description={meal.description}
        price={meal.price}
      />
    );
  });
  return (
    <Card>
      <section className={classes.meals}>
        <ul>{mealitems}</ul>
      </section>
    </Card>
  );
};
export default AvailableMeals;
