const AvailableMeals = () => {
  const [Meals, setMeals] = useState([]);
  setMeals([]);
  const mealitems = Meals.map((meal) => {
    return (
      <Mealitem
        key={meal.id}
        id={meal.id}
        name={meal.name}
        description={meal.description}
        price={meal.price}
      />
    );
  });
  return (
    <Card>
      <section className={classes.meals}>
        <ul>{mealitems}</ul>
      </section>
    </Card>
  );
};
export default AvailableMeals;
